    val listData: ArrayList<TF2CLASS>
        get() {
            val list = arrayListOf<TF2CLASS>()
            for (aData in data) {
                val tf2 = TF2CLASS()
                tf2.name = aData[0] as String
                tf2.from = aData[1] as String
                tf2.photo = aData[2] as Int

                list.add(tf2)
            }
            return list
        }


